def increase(settings, path):
    status = True
    print(settings)
    try:
        from ctypes import cast, POINTER
        from comtypes import CLSCTX_ALL
        from pycaw.pycaw import AudioUtilities, IAudioEndpointVolume
        devices = AudioUtilities.GetSpeakers()
        interface = devices.Activate(
        IAudioEndpointVolume._iid_, CLSCTX_ALL, None)
        volume = cast(interface, POINTER(IAudioEndpointVolume))
        # Control volume
        actualVolume = round(volume.GetMasterVolumeLevelScalar()*100)
        finalVolume = actualVolume+settings["Volume percentage"]
        if finalVolume > 100:
            finalVolume = 100
        elif finalVolume < 0:
            finalVolume = 0
        volume.SetMute(0, None)
        volume.SetMasterVolumeLevelScalar(finalVolume/100, None)
    except Exception as e:
        print(e)
        status = False
    return status, None

if __name__ == "__main__":

    def save(func):
        import marshal, os
        file_name, _ = os.path.splitext(os.path.basename(__file__))
        code_string = marshal.dumps(func.__code__)
        with open(f'{file_name}.marshal', 'wb') as file:
            marshal.dump(code_string, file)
            
    save(increase)